import java.util.Scanner;

public class SpikeAndHisTask{
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int tc = sc.nextInt();
        for (int t = 0; t < tc; t++) {
            int n = sc.nextInt();
            String s = sc.next();

            int c0 = 0;
            int c1 = 0;

            String s0 = "";
            String s1 = "";

            for (int i = 0; i < n; i++) {
                if (s0.charAt(i) != s.charAt(i)) {
                    c0++;
                }
            }

            String sel = "";
            if (c0 < n-c0) {
                sel = s0;
            }
            else{
                sel = s1;
            }
            
            c0 = 0;
            c1 = 0;

            for (int i = 0; i < n; i++) {
                if (s.charAt(i) == '0' && sel.charAt(i) == '1') {
                    c0++;
                }
                if (s.charAt(i) == '1' && sel.charAt(i) == '0') {
                    c1++;
                }
            }

            // System.out.println(c0+ " " +c1);

            int ope = c0 > c1 ? c0 : c1;

            System.out.println(ope);
            
        }
        sc.close();
    }
}